mapper.insert:
INSERT INTO TB_CHARACTER_ATTACH(
							    CA_NO
							   ,CB_NO
							   ,CA_ORIGIN_NAME
							   ,CA_CHANGE_NAME
							   ,CA_LEVEL)
SELECT SEQ_CANO.NEXTVAL,SEQ_CBNO.CURRVAL, A.*
FROM(
	 
	 		SELECT #{ item.originName }
	 			  ,#{ item.changeName }
	 			  ,#{ item.level }
	 		FROM SYS.DUAL
	 
	 )A
