mapper.insert:
INSERT INTO TB_CHARACTER( C_NO ,M_NO ,C_NAME ,C_ORIGIN_NAME ,C_CHANGE_NAME ,C_LEVEL ,C_STATUS) SELECT SEQ_CNO.NEXTVAL, A.* FROM(
SELECT #{ cList.memberNo }, #{ cList.charName }, #{ cList.originName }, #{ cList.changeName }, #{ cList.level }, #{ cList.status } FROM SYS.DUAL
)A

INSERT INTO TB_CHARACTER_ATTACH(
							    CA_NO
							   ,CB_NO
							   ,CA_ORIGIN_NAME
							   ,CA_CHANGE_NAME
							   ,CA_LEVEL
							   ,CA_STATUS)
SELECT SEQ_CANO.NEXTVAL,SEQ_CBNO.CURRVAL, A.*
FROM(
	 
	 		SELECT #{ list.originName }
	 			  ,#{ list.changeName }
	 			  ,#{ list.level }
	 			  ,#{ list.status }
	 		FROM SYS.DUAL
	 
	 )A

mapper.update:
UPDATE TB_CHARACTER_BOARD
SET C_NAME = #{ charName },
	CB_TITLE = #{ boardTitle },
	CB_CONTENT = #{ boardContent }
WHERE CB_STATUS = 'Y'
AND CB_NO = #{ boardNo }

update.foreach:
UPDATE TB_CHARACTER_ATTACH
SET CA_ORIGIN_NAME = #{ item.originName },
	CA_CHANGE_NAME = #{ item.changeName },
	CA_STATUS = #{ item.status }
WHERE CB_NO = #{ item.refBno }
AND CA_LEVEL = #{ item.level }

mapper.update:
UPDATE TB_CHARACTER_BOARD
SET CB_STATUS = 'N'
WHERE CB_NO = #{ bno }

mapper.insert:
INSERT INTO TB_CHARACTER_REPLY(
							   CR_NO
							  ,CB_NO
							  ,CR_CONTENT
							  ,CR_WRITER
							  ,CR_GROUP_NO
							  ,CR_PARENT_NO
							  ,CR_STEP
							  ,CR_LEVEL
							  )
					    VALUES(
					    	   #{ reNo }
					    	  ,#{ refBno }
					    	  ,#{ reContent }
					    	  ,1
					    	  ,#{ reGroupNo }
					    	  ,#{ reParentNo }
					    	  ,#{ reStep }
					    	  ,#{ reLevel }
					  		  )

mapper.update:
UPDATE TB_CHARACTER_REPLY
SET CR_CONTENT = #{ reContent }
WHERE CR_STATUS = 'Y'
AND CR_NO = #{ reNo }
AND CB_NO = #{ refBno }

UPDATE TB_CHARACTER_REPLY
SET CR_STATUS = 'N'
WHERE CR_NO = #{ reNo }
AND CB_NO = #{ refBno }

mapper.select:
SELECT NVL(MAX(CR_STEP), 0) + 1
FROM TB_CHARACTER_REPLY
WHERE CR_GROUP_NO = #{ reGroupNo }

mapper.update:
UPDATE TB_CHARACTER_REPLY
SET CR_STEP = CR_STEP + 1
WHERE CR_GROUP_NO = #{ reGroupNo }
AND CR_STEP > #{ reStep }
